insert into gioco(cod_gioco,nome_gioco,edizione,tipologia,prezzo,descrizione,n_giocatori) values
	("g001","Barrage","1 edezione","tavolo",80,"Barrage vi permetterà di viaggiare indietro nel tempo in un 1930 alternativo in cui un estremo utilizzo delle risorse fossili ha portato al completo esaurimento del carbone utilizzato come fonte di energia. La società è ormai sull’orlo di una crisi globale quando un gruppo di lungimiranti imprenditori decide di investire un cospicuo ammontare di fondi per lo sviluppo di una nuova fonte energetica rinnovabile e pulita, l’energia idroelettrica.",3),
    ("g002","AutoBahn","1 edizione","tavolo",50,"Sviluppate l’autostrada tedesca (Autobahn), costruendo e potenziando la rete autostradale federale per migliorare la vostra posizione nella società della BundesAutobahn.
insert into espansione(cod_espansione,nome_espansione,descrizione,prezzo,cod_gioco) values
("esp001","Barrage 5 giocatore","Arricchisci la tua copia di Barrage con una nuova mappa progettata specificamente per 5 giocatori e scopri le nuove connessioni, studiate per consentire nuove strategie di piazzamento senza alterare il flusso di gioco.",25.5,"g001"),
("esp002","Adrenalina","Un’espansione per sei…e per due giocatori! Sparare in faccia ai vostri amici è sempre divertentissimo, ma ora potete giocare in squadra per sparare tutti insieme in faccia agli altri vostri amici! Oppure voi e un vostro amico potete mettere in scena un epico duello tra mazzate ed esplosioni di primo livello!",15,"g001");
